DROP TABLE IF EXISTS `queries_bi`;
CREATE TABLE `queries_bi` (
  `query_id` int(11) NOT NULL AUTO_INCREMENT,
  `query` text,
  `query_orig` varchar(1000) DEFAULT NULL,
  `GPSJ` varchar(3) DEFAULT '?',
  `gbset` text,
  `selectionpredicate` text,
  `measureclause` text,
  `dataset` varchar(100) DEFAULT NULL,
  `lang` varchar(5) DEFAULT NULL,
  PRIMARY KEY (`query_id`)
) ENGINE=InnoDB AUTO_INCREMENT=211 DEFAULT CHARSET=utf8mb4;
